import { FormControl, Grid, Input ,Paper, FormGroup, Avatar, TextField, Button, Typography, makeStyles } from "@material-ui/core"
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import { useState } from "react";
import { Link, useHistory, Router } from "react-router-dom";
import { Formik, Form ,Field,ErrorMessage} from "formik";
// import Form from "rc-field-form/es/Form";
import * as Yup from 'yup';
import React from "react";



const useStyles = makeStyles  ({
  logg : {
   color: 'blue', 
   textDecoration : 'none',
   fontSize: 20,
   marginLeft:10
  }
})


const initialValues = {             
  name  :'',
  phone: '',
  Email: '',
  password: '',
  confirmpassword:'',
}


const SignUp = () => {
  // const [user, setUser] = useState(initialValues);
  const classes= useStyles;
  const history = useHistory();

  // const { username, password} = user; 


    const paperStyle={padding :20, height:'70vh',width:300, margin:"20px auto"}
    const avatarStyle={backgroundColor:'green'}
    const containerStyle={ marginTop: '20px'}
    const btnStyle={margin: '10px 5px'}

  //   const onValueChange = (e) => {
  //     console.log(e.target.value);
  //     setUser({...user,[e.target.name] : e.target.value})
  // }
 const validationSchema=Yup.object().shape({
      name:Yup.string().email('please enter valid email').required("Required"),
      password:Yup.string().required("Required")
 })
  const onSubmit=(values, props) =>{ 
    console.log(values)
    const temp = JSON.parse(localStorage.getItem('username'));
    // const tasks = Object.values(temp);
    console.log(temp.username);
    console.log(values.username);
    console.log(values.password);
    console.log(temp.password);
    if(values.username === temp.username && values.password === temp.password)
    {
      console.log("working");
      history.push('/');
    }
    props.resetForm()
    setTimeout(()=>{
      props.resetForm()
      props.setSubmitting(true)
    },2000)

    // console.log(props)
  }
  // let data =  localStorage.getItem('username').replace(/"/g,"");


  return(
    <Grid>
       <Paper elevation={10} style={paperStyle}>
         <Grid align='center'>
       <Avatar style={avatarStyle}><LockOutlinedIcon /> </Avatar>
      <h2> SignUp</h2>
      </Grid>
      <Formik initialValues ={initialValues} onSubmit={onSubmit} validationSchema ={validationSchema}>
        {(props) => (
          <Form>
            {/* {console.log(props)} */}
            <Field as={TextField} style={containerStyle} label='Name' placeholder='Enter your name' fullWidth required
            helperText={<ErrorMessage name="name"/>} 
        name='name'  />

           <Field as={TextField} style={containerStyle} label='Phone' placeholder='Enter your name' fullWidth required
            helperText={<ErrorMessage name="name"/>} 
        name='name'  /> 
      <Field as={TextField} style={containerStyle} label='Password' placeholder='Enter Password' type='password' fullWidth required 
       helperText={<ErrorMessage name="password"/>} 
        name='password'  />
      <Field as ={FormControlLabel} style={containerStyle}
      name='rememberme'
        control={
          <Checkbox


            color="primary"
          />
        }
        label="Remember Me"
      />



      <Button style={btnStyle} type='submit' color='primary' variant='contained' fullWidth>SignUp</Button>
      </Form>
        )}
      </Formik>

       <Typography> Already
       <Link className={classes.logg} to="/login">LogIn</Link>
  </Typography>
     </Paper>
     </Grid> 
  );
}

export default SignUp
