import "antd/dist/antd.css";
import "./App.css";
import { useState } from "react";
import { Avatar, Button, Modal, Input } from "antd";
import { v4 } from "uuid";
import Table from "react-bootstrap/Table";
import "bootstrap/dist/css/bootstrap.min.css";
import { blue } from "@material-ui/core/colors";
import { formatMs } from "@material-ui/core";

function App() {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [editFlag, setEditFlag] = useState(false);
  const [tempEmail, setTempEmail] = useState("");
  const [tempName, setTempName] = useState("");
  const [tempphone, setTempPhone] = useState("");
  const [tempaddr, setTempAddr] = useState("");
  const [tempstate, setTempState] = useState("");
  const [currentKey, setCurrentKey] = useState(0);
  const [data, setData] = useState([]);

  const [form,setForm] = useState({});

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {

    console.log('ok firn',form);
    if(form.key){
      let index = data.findIndex(r => r.key == form.key);
      data[index] = form
      setData([...data])
    }else{
      setData([...data,{...form,key:v4()}])
    }
    setIsModalVisible(false);

    return ;
    setIsModalVisible(false);
    if (!editFlag) {
      setData([
        ...data,
        {
          key: v4(),
          email: tempEmail,
          name: tempName,
          phone: tempphone,
          address: tempaddr,
          state: tempstate,
        },
      ]); //For adding new row
    } else {
      //For Editing current row
      const newValue = {
        key: currentKey,
        email: tempEmail,
        name: tempName,
        phone: tempphone,
        address: tempaddr,
        state: tempstate,
      }; //Value of Editing row
      const newData = data.map((item) => {
        if (item.key === currentKey) {
          return newValue;
        }
        return item;
      });
      // console.log(newData);
      setData(newData);
      setEditFlag(false);
    }
    setTempEmail("");
    setTempName("");
    setTempPhone("");
    setTempAddr("");
    setTempState("");
  };

  const handleCancel = () => {
    setIsModalVisible(false);
    setEditFlag(false);
    setTempEmail("");
    setTempName("");
    setTempPhone("");
    setTempAddr("");
    setTempState("");
  };

  const onEdit = (data) => {
    setForm(data)
    setIsModalVisible(true);
    return 

  };

  const onDelete = (key) => {
    console.log(key);
    const newData = data.filter((item) => item.key !== key);
    //console.log(newData);
    setData(newData);
  };

  // const columns = [
  //   {
  //     title: "Email",
  //     dataIndex: "email",
  //     key: "email",
  //   },
  //   {
  //     title: "Name",
  //     dataIndex: "name",
  //     key: "name",
  //   },
  //   {
  //     title: "Action",
  //     dataIndex: "",
  //     key: "x",
  //     render: () => (
  //       <button onClick={() => console.log(currentKey)}>Delete</button>
  //     ),
  //   },
  // ];

  const onChange = (key,event) => {
    console.log('onchagne',key,event.target.value)
    // if(key == 'name') setForm({...form,name:event.target.value})
    // else if(key == 'email') setForm({...form,email:event.target.value})
    setForm({...form,[key]:event.target.value})

  }
console.log('form',form)
  return (
    <div>
      <Button type="primary" onClick={showModal}>
        Add New
      </Button>
      <Modal
        title="Details"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Input
          className="content"
          placeholder="Enter Email"
          value={form.email}
          onChange={onChange.bind(this,'email')}
        />
         <Input
          className="middle"
          placeholder="Enter Name"
          value={form.name}
          onChange={onChange.bind(this,'name')}
        />

<Input
          className="middle"
          placeholder="Enter mobile"
          value={form.phone}
          onChange={onChange.bind(this,'phone')}
        />

<Input
          className="end"
          placeholder="address"
          value={form.address}
          onChange={onChange.bind(this,'address')}
        />

        {/* <Input
          className="middle"
          placeholder="Enter Name"
          value={tempName}
          onChange={(e) => {
            console.log("event", e.target.value);
            setTempName(e.target.value);
          }}
        />
        <Input
          className="bottom"
          placeholder="Enter phone no."
          value={tempphone}
          onChange={(e) => {
            console.log("event", e.target.value);
            setTempPhone(e.target.value);
          }}
        />

        <Input
          className="end"
          placeholder="address"
          value={tempaddr}
          onChange={(e) => {
            console.log("event", e.target.value);
            setTempAddr(e.target.value);
          }}
        />
        <Input
          placeholder="state"
          value={tempstate}
          onChange={(e) => {
            console.log("event", e.target.value);
            setTempState(e.target.value);
          }}
        /> */}
      </Modal>
      <br />
      {/* <Table rowSelection={({key}) => setCurrentKey(key)} dataSource={data} columns={columns} />; */}
      <Table striped bordered hover className="table borderless">
        <tr>
          <th></th>
          <th>Email</th>
          <th>Name</th>
          <th>Phone</th>
          <th>Address</th>
         {/*  
          <th>address</th>
          <th>state</th> */}
        </tr>

        {data.map((item) => (
          <tr>
            <td>
              <Avatar
                style={{
                  color: "#f56a00",
                  backgroundColor: "#fde3cf",
                  textAlign: "center",
                }}
              >
                U
              </Avatar>
            </td>
            <td>{item.email}</td>
            <td>{item.name}</td>
            <td>{item.phone}</td>
            <td>{item.address}</td>
           {/* 
            <td>{item.address}</td>
            <td>{item.state}</td> */}
            <td>
              <Button onClick={() => onEdit(item)}>Edit</Button>
            </td>
            <td>
              <Button onClick={() => onDelete(item.key)}>Delete</Button>
            </td>
          </tr>
        ))}
      </Table>
    </div>
  );
}

export default App;
